import React, { useState } from 'react';
import { themes, getThemeColors, getAvailableThemes } from './theme';
import './ThemeContainer.css';

const ThemeContainer = ({ children }) => {
  const [currentTheme, setCurrentTheme] = useState('portfolio');
  const colors = getThemeColors(currentTheme);

  // Gradient definitions based on your color palette
  const gradients = {
    portfolio: {
      background: `linear-gradient(135deg, ${colors.background} 0%, ${colors.surface} 50%, ${colors.primary} 100%)`,
      card: `linear-gradient(145deg, ${colors.surface} 0%, ${colors.primary}20 100%)`,
      button: `linear-gradient(135deg, ${colors.primary} 0%, ${colors.secondary} 100%)`,
      accent: `linear-gradient(135deg, ${colors.accent} 0%, ${colors.highlight} 100%)`
    },
    professional: {
      background: `linear-gradient(135deg, ${colors.background} 0%, ${colors.surface} 50%, ${colors.primary} 100%)`,
      card: `linear-gradient(145deg, ${colors.surface} 0%, ${colors.primary}20 100%)`,
      button: `linear-gradient(135deg, ${colors.primary} 0%, ${colors.secondary} 100%)`,
      accent: `linear-gradient(135deg, ${colors.accent} 0%, ${colors.highlight} 100%)`
    },
    creative: {
      background: `linear-gradient(135deg, ${colors.background} 0%, ${colors.surface} 50%, ${colors.primary} 100%)`,
      card: `linear-gradient(145deg, ${colors.surface} 0%, ${colors.primary}20 100%)`,
      button: `linear-gradient(135deg, ${colors.primary} 0%, ${colors.secondary} 100%)`,
      accent: `linear-gradient(135deg, ${colors.accent} 0%, ${colors.highlight} 100%)`
    },
    elegant: {
      background: `linear-gradient(135deg, ${colors.background} 0%, ${colors.surface} 50%, ${colors.primary} 100%)`,
      card: `linear-gradient(145deg, ${colors.surface} 0%, ${colors.primary}20 100%)`,
      button: `linear-gradient(135deg, ${colors.primary} 0%, ${colors.secondary} 100%)`,
      accent: `linear-gradient(135deg, ${colors.accent} 0%, ${colors.highlight} 100%)`
    }
  };

  const currentGradients = gradients[currentTheme];

  const containerStyle = {
    background: currentGradients.background,
    color: colors.text,
    minHeight: '100vh',
    padding: '20px',
    transition: 'all 0.5s ease',
    position: 'relative',
    overflow: 'hidden'
  };

  const themeSelectorStyle = {
    position: 'fixed',
    top: '20px',
    right: '20px',
    zIndex: 1000,
    background: currentGradients.card,
    border: `1px solid ${colors.border}`,
    borderRadius: '15px',
    padding: '15px',
    boxShadow: `0 8px 32px ${colors.shadow}`,
    backdropFilter: 'blur(10px)',
    transition: 'all 0.3s ease'
  };

  const selectStyle = {
    background: currentGradients.button,
    color: colors.highlight,
    border: 'none',
    borderRadius: '10px',
    padding: '10px 15px',
    fontSize: '14px',
    cursor: 'pointer',
    fontWeight: '500',
    transition: 'all 0.3s ease'
  };

  const handleThemeChange = (event) => {
    setCurrentTheme(event.target.value);
  };

  return (
    <div style={containerStyle}>
      {/* Animated background elements */}
      <div className="bg-animation">
        <div className="bg-circle" style={{ background: currentGradients.accent }}></div>
        <div className="bg-circle" style={{ background: currentGradients.button }}></div>
        <div className="bg-circle" style={{ background: currentGradients.accent }}></div>
      </div>

      {/* Theme Selector */}
      <div style={themeSelectorStyle}>
        <label htmlFor="theme-select" style={{ color: colors.textSecondary, marginRight: '10px', fontWeight: '500' }}>
          Theme:
        </label>
        <select
          id="theme-select"
          value={currentTheme}
          onChange={handleThemeChange}
          style={selectStyle}
        >
          {getAvailableThemes().map(themeKey => (
            <option key={themeKey} value={themeKey}>
              {themes[themeKey].name}
            </option>
          ))}
        </select>
      </div>

      {/* Main Content */}
      <div style={{ position: 'relative', zIndex: 2 }}>
        {children}
      </div>
    </div>
  );
};

export default ThemeContainer;
