import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { getThemeColors } from './theme';

const Portfolio = () => {
  const [activeSection, setActiveSection] = useState('intro');
  const colors = getThemeColors();

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        duration: 0.6,
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.5,
        ease: "easeOut"
      }
    }
  };

  const cardVariants = {
    hidden: { scale: 0.8, opacity: 0 },
    visible: {
      scale: 1,
      opacity: 1,
      transition: {
        duration: 0.5,
        ease: "easeOut"
      }
    },
    hover: {
      scale: 1.02,
      y: -5,
      transition: {
        duration: 0.2
      }
    }
  };

  const sections = [
    { id: 'intro', label: 'Introduction' },
    { id: 'experience', label: 'Experience' },
    { id: 'skills', label: 'Skills' },
    { id: 'personal', label: 'Personal' }
  ];

  const experiences = [
    {
      company: 'DATAMINDS',
      role: 'CONSULTANT',
      period: '2024 - Now',
      location: 'Aarhus',
      highlights: [
        'Led AI-powered content analysis platform development',
        'Implemented microservices with Azure DevOps CI/CD',
        'Reduced manual review time by 90%',
        'Led DWH project in finance sector'
      ]
    },
    {
      company: 'IFACT',
      role: 'CONSULTANT',
      period: '2023 - 2024',
      location: 'Vejle',
      highlights: [
        'Delivered end-to-end BI as a service',
        'Transformed raw data into actionable insights',
        'Used M, PowerQuery, and DAX for data modeling',
        'Collaborated across finance, HR, and logistics teams'
      ]
    },
    {
      company: 'SINDICO',
      role: 'CONTROLLER',
      period: '2022 - 2023',
      location: 'Ikast',
      highlights: [
        'Handled budgeting and cash flow forecasting',
        'Implemented Power BI-based data warehouse',
        'Supported monthly closings and audit prep',
        'Delivered Excel and Power BI training sessions'
      ]
    },
    {
      company: 'RISKVILLE',
      role: 'CLIENT ADVOCATE',
      period: '2020 - 2022',
      location: 'Silkeborg',
      highlights: [
        'Joined insurtech startup as student',
        'Supported client onboarding and data migration',
        'Improved operational efficiency for insurance brokers',
        'Contributed to technical and client-facing areas'
      ]
    }
  ];

  const skillCategories = [
    {
      category: 'FINANCIAL & BUSINESS',
      skills: ['Budgeting & Forecasting', 'Financial Analysis', 'KPI Reporting', 'Cost & Variance Analysis', 'ERP Systems', 'Stakeholder Collaboration']
    },
    {
      category: 'DATA & ANALYTICS',
      skills: ['Fabric', 'Power BI', 'DAX', 'Tabular Editor', 'M / PowerQuery', 'Data Warehousing', 'ETL', 'SQL / NoSQL', 'Spark', 'Lakehouse', 'SAS']
    },
    {
      category: 'PROGRAMMING & DEVELOPMENT',
      skills: ['Python', 'C#', 'YAML', 'RESTful APIs', 'Microservices Architecture', '.NET Framework']
    },
    {
      category: 'MESSAGING & INTEGRATION',
