  // Animation loop to ensure continuous rendering
  const animate = () => {
    if (globeRef.current) {
      globeRef.current.render();
    }
    animationRef.current = requestAnimationFrame(animate);
  };

  useEffect(() => {
    if (canvasRef.current) {
      // Clean up previous globe and animation
      if (globeRef.current) {
        globeRef.current.destroy();
      }
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }

      const globe = createGlobe(canvasRef.current, {
        devicePixelRatio: 2,
        width: 600,
        height: 600,
        phi: 0,
        theta: 0.3,
        dark: 1, // Full dark for better contrast with dots
        diffuse: 3, // Increased diffusion for better dot visibility
        mapSamples: 10000, // Reduced for better performance
        mapBrightness: 12, // Increased brightness for dot outlines
        baseColor: [0.22, 0.22, 0.22], // Dark grey base like template
        glowColor: [0.43, 0.43, 0.43], // Medium grey glow like template
        markerColor: [0, 0.5, 1], // Blue markers like template
        markers: markers,
        scale: 1,
        offset: [0, 0],
        onRender: (state) => {
          state.phi += 0.001; // Very slow rotation
        }
      });

      globeRef.current = globe;

      // Start continuous animation loop
      animate();

      // Force initial render
      setTimeout(() => {
        if (globeRef.current) {
          globeRef.current.render();
          setIsGlobeReady(true);
        }
      }, 100);

      return () => {
        if (globeRef.current) {
          globeRef.current.destroy();
        }
        if (animationRef.current) {
          cancelAnimationFrame(animationRef.current);
        }
      };
    }
  }, [selectedCompany, markers]);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, []);

