import React, { useState } from 'react';
import { themes, getThemeColors, getAvailableThemes } from './theme';
import './ThemeContainer.css';

const ThemeContainer = ({ children }) => {
  const [currentTheme, setCurrentTheme] = useState('light');
  const colors = getThemeColors(currentTheme);

  const containerStyle = {
    backgroundColor: colors.background,
    color: colors.text,
    minHeight: '100vh',
    padding: '20px',
    transition: 'all 0.3s ease'
  };

  const themeSelectorStyle = {
    position: 'fixed',
    top: '20px',
    right: '20px',
    zIndex: 1000,
    backgroundColor: colors.surface,
    border: `1px solid ${colors.border}`,
    borderRadius: '8px',
    padding: '10px',
    boxShadow: `0 2px 10px ${colors.shadow}`
  };

  const selectStyle = {
    backgroundColor: colors.background,
    color: colors.text,
    border: `1px solid ${colors.border}`,
    borderRadius: '4px',
    padding: '8px 12px',
    fontSize: '14px',
    cursor: 'pointer'
  };

  const contentStyle = {
    backgroundColor: colors.surface,
    border: `1px solid ${colors.border}`,
    borderRadius: '12px',
    padding: '24px',
    margin: '20px 0',
    boxShadow: `0 4px 20px ${colors.shadow}`,
    transition: 'all 0.3s ease'
  };

  const handleThemeChange = (event) => {
    setCurrentTheme(event.target.value);
  };

  return (
    <div style={containerStyle}>
      {/* Theme Selector */}
      <div style={themeSelectorStyle}>
        <label htmlFor="theme-select" style={{ color: colors.textSecondary, marginRight: '8px' }}>
          Theme:
        </label>
        <select
          id="theme-select"
          value={currentTheme}
          onChange={handleThemeChange}
          style={selectStyle}
        >
          {getAvailableThemes().map(themeKey => (
            <option key={themeKey} value={themeKey}>
              {themes[themeKey].name}
            </option>
          ))}
        </select>
      </div>

      {/* Main Content Container */}
      <div style={contentStyle}>
        <h1 style={{ color: colors.primary, marginBottom: '20px' }}>
          React Theme Container
        </h1>
        <p style={{ color: colors.textSecondary, marginBottom: '30px' }}>
          This is a simple React container with theme support. You can switch between different color schemes using the theme selector in the top right corner.
        </p>
        
        {/* Color Palette Display */}
        <div style={{ marginBottom: '30px' }}>
          <h3 style={{ color: colors.text, marginBottom: '15px' }}>Current Theme Colors:</h3>
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))', gap: '10px' }}>
            {Object.entries(colors).map(([colorName, colorValue]) => (
              <div
                key={colorName}
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  padding: '8px',
                  borderRadius: '6px',
                  border: `1px solid ${colors.border}`,
                  backgroundColor: colors.background
                }}
              >
                <div
                  style={{
                    width: '20px',
                    height: '20px',
                    backgroundColor: colorValue,
                    borderRadius: '4px',
                    marginRight: '8px',
                    border: `1px solid ${colors.border}`
                  }}
                />
                <span style={{ fontSize: '12px', color: colors.textSecondary }}>
                  {colorName}
                </span>
              </div>
            ))}
          </div>
        </div>

        {/* Children Content */}
        {children && (
          <div style={{ borderTop: `1px solid ${colors.border}`, paddingTop: '20px' }}>
            {children}
          </div>
        )}
      </div>
    </div>
  );
};

export default ThemeContainer;
