import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { getThemeColors } from './theme';
import CompanyLogos from './CompanyLogos';
import PersonalSection from './PersonalSection';
import { companyData } from './data/companyData';
import profileImage from './data/privatesection/1659513793685.jpeg';

const Portfolio = () => {
  const [activeSection, setActiveSection] = useState('intro');
  const [selectedCompany, setSelectedCompany] = useState('dataminds');
  const colors = getThemeColors('elegant');

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        duration: 0.6,
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        duration: 0.5,
        ease: "easeOut"
      }
    }
  };

  const cardVariants = {
    hidden: { scale: 0.8, opacity: 0 },
    visible: {
      scale: 1,
      opacity: 1,
      transition: {
        duration: 0.5,
        ease: "easeOut"
      }
    },
    hover: {
      scale: 1.02,
      y: -5,
      transition: {
        duration: 0.2
      }
    }
  };

  // Gradient styles for cards
  const getCardGradient = () => {
    return `linear-gradient(145deg, ${colors.surface} 0%, ${colors.primary}15 50%, ${colors.secondary}10 100%)`;
  };

  const getButtonGradient = () => {
    return `linear-gradient(135deg, ${colors.primary} 0%, ${colors.secondary} 100%)`;
  };

  const getAccentGradient = () => {
    return `linear-gradient(135deg, ${colors.accent} 0%, ${colors.highlight} 100%)`;
  };

  const sections = [
    { id: 'intro', label: 'Introduction' },
    { id: 'experience', label: 'Experience' },
    { id: 'skills', label: 'Skills' },
    { id: 'personal', label: 'Personal' }
  ];

  const handleCompanySelect = (companyId) => {
    setSelectedCompany(companyId);
  };

  const skillCategories = [
    {
      category: 'FINANCIAL & BUSINESS',
      skills: ['Budgeting & Forecasting', 'Financial Analysis', 'KPI Reporting', 'Cost & Variance Analysis', 'ERP Systems', 'Stakeholder Collaboration']
    },
    {
      category: 'DATA & ANALYTICS',
      skills: ['Fabric', 'Power BI', 'DAX', 'Tabular Editor', 'M / PowerQuery', 'Data Warehousing', 'ETL', 'SQL / NoSQL', 'Spark', 'Lakehouse', 'SAS']
    },
    {
      category: 'PROGRAMMING & DEVELOPMENT',
      skills: ['Python', 'C#', 'YAML', 'RESTful APIs', 'Microservices Architecture', '.NET Framework']
    },
    {
      category: 'MESSAGING & INTEGRATION',
      skills: ['RabbitMQ', 'FastStream', 'Service Communication Patterns']
    }
  ];

  return (
    <motion.div
      variants={containerVariants}
      initial="hidden"
      animate="visible"
      style={{ color: colors.text }}
    >
      {/* Header with Profile Picture and Contact Info */}
      <motion.div variants={itemVariants} className="portfolio-header">
        <div className="header-content">
          <div className="profile-section">
            <motion.div 
              className="profile-image-container"
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              transition={{ duration: 0.8, delay: 0.2 }}
            >
              <img 
                src={profileImage} 
                alt="Mathias Porse" 
                className="profile-image"
                style={{
                  width: '200px',
                  height: '200px',
                  borderRadius: '50%',
                  objectFit: 'cover',
                  border: `4px solid ${colors.primary}`,
                  boxShadow: `0 8px 32px ${colors.shadow}`,
                  maskImage: 'radial-gradient(circle at center, black 60%, transparent 100%)',
                  WebkitMaskImage: 'radial-gradient(circle at center, black 60%, transparent 100%)'
                }}
              />
            </motion.div>
            
            <motion.div className="name-section">
              <motion.h1 
                className="main-title"
                style={{ 
                  color: colors.primary,
                  fontSize: '3.5rem',
                  fontWeight: '300',
                  letterSpacing: '0.1em',
                  textTransform: 'uppercase',
                  margin: '0',
                  background: `linear-gradient(135deg, ${colors.primary} 0%, ${colors.secondary} 100%)`,
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  backgroundClip: 'text',
                  textShadow: `0 2px 4px ${colors.shadow}`
                }}
                initial={{ x: -50, opacity: 0 }}
                animate={{ x: 0, opacity: 1 }}
                transition={{ duration: 0.8 }}
              >
                Mathias Porse
              </motion.h1>
              <motion.p 
                className="subtitle"
                style={{ 
                  color: colors.textSecondary,
                  fontSize: '1.2rem',
                  fontWeight: '300',
                  letterSpacing: '0.05em',
                  margin: '10px 0 20px 0'
                }}
                initial={{ x: 50, opacity: 0 }}
                animate={{ x: 0, opacity: 1 }}
                transition={{ duration: 0.8, delay: 0.2 }}
              >
                Finance-driven data candidate with a unique mix of controlling, reporting, and technical skills
              </motion.p>
            </motion.div>
          </div>

          {/* Contact and Social Links Box */}
          <motion.div 
            className="contact-social-box"
            style={{
              background: getCardGradient(),
              border: `1px solid ${colors.border}`,
              borderRadius: '20px',
              padding: '25px',
              boxShadow: `0 8px 32px ${colors.shadow}`,
              backdropFilter: 'blur(10px)',
              marginTop: '30px'
            }}
            initial={{ y: 20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            <div className="contact-info">
              <div className="contact-item">
                <span style={{ color: colors.accent, marginRight: '10px' }}>📧</span>
                <span style={{ color: colors.text }}>mathiasporse@gmail.com</span>
              </div>
              <div className="contact-item">
                <span style={{ color: colors.accent, marginRight: '10px' }}>📱</span>
                <span style={{ color: colors.text }}>+45 60 14 17 65</span>
              </div>
            </div>
            
            <div className="social-links">
              <h4 style={{ color: colors.secondary, marginBottom: '15px', fontSize: '1rem', fontWeight: '500' }}>
                Connect with me
              </h4>
              <div className="social-buttons">
                <motion.a
                  href="https://github.com/mapooooo/me"
                  target="_blank"
                  rel="noopener noreferrer"
                  className="social-button"
                  style={{
                    background: getButtonGradient(),
                    color: colors.highlight,
                    padding: '12px 20px',
                    borderRadius: '12px',
                    textDecoration: 'none',
                    fontWeight: '500',
                    display: 'inline-flex',
                    alignItems: 'center',
                    gap: '8px',
                    border: `1px solid ${colors.border}`,
                    boxShadow: `0 4px 16px ${colors.shadow}`
                  }}
                  whileHover={{ scale: 1.05, y: -2 }}
                  whileTap={{ scale: 0.95 }}
                >
                  <span>🐙</span>
                  GitHub
                </motion.a>
                
                <motion.a
                  href="https://www.linkedin.com/in/mathias-porse-17705781/"
                  target="_blank"
                  rel="noopener noreferrer"
                  className="social-button"
                  style={{
                    background: getButtonGradient(),
                    color: colors.highlight,
                    padding: '12px 20px',
                    borderRadius: '12px',
                    textDecoration: 'none',
                    fontWeight: '500',
                    display: 'inline-flex',
                    alignItems: 'center',
                    gap: '8px',
                    border: `1px solid ${colors.border}`,
                    boxShadow: `0 4px 16px ${colors.shadow}`
                  }}
                  whileHover={{ scale: 1.05, y: -2 }}
                  whileTap={{ scale: 0.95 }}
                >
                  <span>💼</span>
                  LinkedIn
                </motion.a>
              </div>
            </div>
          </motion.div>
        </div>
      </motion.div>

      {/* Navigation */}
      <motion.nav variants={itemVariants} className="portfolio-nav">
        {sections.map((section) => (
          <motion.button
            key={section.id}
            onClick={() => setActiveSection(section.id)}
            className={`nav-button ${activeSection === section.id ? 'active' : ''}`}
            style={{
              background: activeSection === section.id ? getButtonGradient() : 'transparent',
              color: activeSection === section.id ? colors.highlight : colors.textSecondary,
              border: `1px solid ${colors.border}`,
              backdropFilter: 'blur(10px)'
            }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            {section.label}
          </motion.button>
        ))}
      </motion.nav>

      {/* Content Sections */}
      <AnimatePresence mode="wait">
        <motion.div
          key={activeSection}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -20 }}
          transition={{ duration: 0.3 }}
        >
          {activeSection === 'intro' && (
            <motion.div variants={itemVariants} className="intro-section">
                             <motion.div
                 className="background-card"
                 style={{
                   background: getCardGradient(),
                   border: `1px solid ${colors.border}`,
                   boxShadow: `0 8px 32px ${colors.shadow}`,
                   backdropFilter: 'blur(10px)'
                 }}
                 variants={cardVariants}
                 whileHover="hover"
               >
                <h2 style={{ color: colors.secondary }}>Background</h2>
                <p>
                  I design automated reporting solutions, financial models, and BI platforms that help 
                  organizations gain clarity across departments. With hands-on experience supporting CFOs 
                  and collaborating across finance, HR, and operations. Equally confident in Excel, ERP 
                  systems, and cloud-based data stacks.
                </p>
              </motion.div>

                             <motion.div
                 className="education-card"
                 style={{
                   background: getCardGradient(),
                   border: `1px solid ${colors.border}`,
                   boxShadow: `0 8px 32px ${colors.shadow}`,
                   backdropFilter: 'blur(10px)'
                 }}
                 variants={cardVariants}
                 whileHover="hover"
               >
                <h2 style={{ color: colors.secondary }}>Education</h2>
                <h3 style={{ color: colors.accent }}>AP FINANCIAL MANAGEMENT</h3>
                <p style={{ color: colors.textSecondary }}>Aarhus | Business Academy Aarhus 2019 - 2021</p>
                <ul>
                  <li>Transferred from University IT program, combining technical foundation with financial expertise</li>
                  <li>Focus on financial management and business administration</li>
                  <li>Relevant coursework: Financial analysis, Business economics, Statistics</li>
                </ul>
              </motion.div>
            </motion.div>
          )}

                    {activeSection === 'experience' && (
            <motion.div 
              className="experience-section"
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
            >
              <h2 style={{ color: colors.secondary, textAlign: 'center', marginBottom: '30px' }}>
                Professional Experience
              </h2>
              

              
              {/* Company Logos */}
              <CompanyLogos 
                onCompanySelect={handleCompanySelect}
                selectedCompany={selectedCompany}
              />
              
              {/* Selected Company Details */}
              {selectedCompany && companyData[selectedCompany] && (
                <motion.div
                  key={selectedCompany}
                  className="experience-card"
                  style={{
                    background: getCardGradient(),
                    border: `1px solid ${colors.border}`,
                    boxShadow: `0 8px 32px ${colors.shadow}`,
                    backdropFilter: 'blur(10px)',
                    padding: '30px',
                    borderRadius: '15px',
                    marginTop: '20px'
                  }}
                  whileHover={{ scale: 1.02, y: -5 }}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5 }}
                >
                  <div className="experience-header">
                    <h3 style={{ color: colors.primary }}>{companyData[selectedCompany].name}</h3>
                    <span style={{ color: colors.accent }}>{companyData[selectedCompany].role}</span>
                  </div>
                  <div className="experience-meta">
                    <span style={{ color: colors.textSecondary }}>{companyData[selectedCompany].period}</span>
                    <span style={{ color: colors.textSecondary }}>{companyData[selectedCompany].location}</span>
                  </div>
                  <div className="experience-description" style={{ color: colors.textSecondary, marginBottom: '20px', lineHeight: '1.6' }}>
                    {companyData[selectedCompany].description}
                  </div>
                  <div>
                    <h4 style={{ color: colors.secondary, marginBottom: '15px' }}>Key Highlights:</h4>
                    <ul>
                      {companyData[selectedCompany].highlights.map((highlight, i) => (
                        <motion.li
                          key={i}
                          initial={{ opacity: 0, x: -20 }}
                          animate={{ opacity: 1, x: 0 }}
                          transition={{ delay: i * 0.1 }}
                        >
                          {highlight}
                        </motion.li>
                      ))}
                    </ul>
                  </div>
                </motion.div>
              )}
            </motion.div>
          )}

          {activeSection === 'skills' && (
            <motion.div variants={itemVariants} className="skills-section">
              {skillCategories.map((category, index) => (
                                 <motion.div
                   key={category.category}
                   className="skill-category"
                   style={{
                     background: getCardGradient(),
                     border: `1px solid ${colors.border}`,
                     boxShadow: `0 8px 32px ${colors.shadow}`,
                     backdropFilter: 'blur(10px)'
                   }}
                   variants={cardVariants}
                   whileHover="hover"
                   initial="hidden"
                   animate="visible"
                   transition={{ delay: index * 0.1 }}
                 >
                  <h3 style={{ color: colors.secondary }}>{category.category}</h3>
                  <div className="skills-grid">
                    {category.skills.map((skill, i) => (
                                             <motion.span
                         key={skill}
                         className="skill-tag"
                         style={{
                           background: getButtonGradient(),
                           color: colors.highlight,
                           border: `1px solid ${colors.border}`,
                           fontWeight: '500'
                         }}
                         initial={{ opacity: 0, scale: 0.8 }}
                         animate={{ opacity: 1, scale: 1 }}
                         transition={{ delay: index * 0.1 + i * 0.05 }}
                         whileHover={{ scale: 1.05 }}
                       >
                        {skill}
                      </motion.span>
                    ))}
                  </div>
                </motion.div>
              ))}
            </motion.div>
          )}

                    {activeSection === 'personal' && (
            <PersonalSection />
          )}
        </motion.div>
      </AnimatePresence>
    </motion.div>
  );
};

export default Portfolio;
