      <motion.nav variants={itemVariants} className="portfolio-nav">
        {sections.map((section) => (
          <motion.button
            key={section.id}
            onClick={() => setActiveSection(section.id)}
            className={`nav-button ${activeSection === section.id ? 'active' : ''}`}
            style={{
              background: activeSection === section.id ? getButtonGradient() : 'transparent',
              color: activeSection === section.id ? colors.highlight : colors.textSecondary,
              border: `1px solid ${colors.border}`,
              backdropFilter: 'blur(10px)'
            }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            {section.label}
          </motion.button>
        ))}
      </motion.nav>

      {/* Content Sections */}
      <AnimatePresence mode="wait">
        <motion.div
          key={activeSection}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -20 }}
          transition={{ duration: 0.3 }}
        >
          {activeSection === 'intro' && (
            <motion.div variants={itemVariants} className="intro-section">
                             <motion.div
                 className="background-card"
                 style={{
                   background: getCardGradient(),
                   border: `1px solid ${colors.border}`,
                   boxShadow: `0 8px 32px ${colors.shadow}`,
                   backdropFilter: 'blur(10px)'
                 }}
                 variants={cardVariants}
                 whileHover="hover"
               >
                <h2 style={{ color: colors.secondary }}>Background</h2>
                <p>
                  I design automated reporting solutions, financial models, and BI platforms that help 
                  organizations gain clarity across departments. With hands-on experience supporting CFOs 
                  and collaborating across finance, HR, and operations. Equally confident in Excel, ERP 
                  systems, and cloud-based data stacks.
                </p>
              </motion.div>

                             <motion.div
                 className="education-card"
                 style={{
                   background: getCardGradient(),
                   border: `1px solid ${colors.border}`,
                   boxShadow: `0 8px 32px ${colors.shadow}`,
                   backdropFilter: 'blur(10px)'
                 }}
                 variants={cardVariants}
                 whileHover="hover"
               >
                <h2 style={{ color: colors.secondary }}>Education</h2>
                <h3 style={{ color: colors.accent }}>AP FINANCIAL MANAGEMENT</h3>
                <p style={{ color: colors.textSecondary }}>Aarhus | Business Academy Aarhus 2019 - 2021</p>
                <ul>
                  <li>Transferred from University IT program, combining technical foundation with financial expertise</li>
                  <li>Focus on financial management and business administration</li>
                  <li>Relevant coursework: Financial analysis, Business economics, Statistics</li>
                </ul>
              </motion.div>
            </motion.div>
          )}

          {activeSection === 'experience' && (
            <motion.div variants={itemVariants} className="experience-section">
              {experiences.map((exp, index) => (
                                 <motion.div
                   key={exp.company}
                   className="experience-card"
                   style={{
                     background: getCardGradient(),
                     border: `1px solid ${colors.border}`,
                     boxShadow: `0 8px 32px ${colors.shadow}`,
                     backdropFilter: 'blur(10px)'
                   }}
                   variants={cardVariants}
                   whileHover="hover"
                   initial="hidden"
                   animate="visible"
                   transition={{ delay: index * 0.1 }}
                 >
                  <div className="experience-header">
                    <h3 style={{ color: colors.primary }}>{exp.company}</h3>
                    <span style={{ color: colors.accent }}>{exp.role}</span>
                  </div>
                  <div className="experience-meta">
                    <span style={{ color: colors.textSecondary }}>{exp.period}</span>
                    <span style={{ color: colors.textSecondary }}>{exp.location}</span>
                  </div>
                  <ul>
                    {exp.highlights.map((highlight, i) => (
                      <motion.li
                        key={i}
                        initial={{ opacity: 0, x: -20 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ delay: index * 0.1 + i * 0.1 }}
                      >
                        {highlight}
                      </motion.li>
                    ))}
                  </ul>
                </motion.div>
              ))}
            </motion.div>
          )}

          {activeSection === 'skills' && (
            <motion.div variants={itemVariants} className="skills-section">
              {skillCategories.map((category, index) => (
                                 <motion.div
                   key={category.category}
                   className="skill-category"
                   style={{
                     background: getCardGradient(),
                     border: `1px solid ${colors.border}`,
                     boxShadow: `0 8px 32px ${colors.shadow}`,
                     backdropFilter: 'blur(10px)'
                   }}
                   variants={cardVariants}
                   whileHover="hover"
                   initial="hidden"
                   animate="visible"
                   transition={{ delay: index * 0.1 }}
                 >
                  <h3 style={{ color: colors.secondary }}>{category.category}</h3>
                  <div className="skills-grid">
                    {category.skills.map((skill, i) => (
                                             <motion.span
                         key={skill}
                         className="skill-tag"
                         style={{
                           background: getButtonGradient(),
                           color: colors.highlight,
                           border: `1px solid ${colors.border}`,
                           fontWeight: '500'
                         }}
                         initial={{ opacity: 0, scale: 0.8 }}
                         animate={{ opacity: 1, scale: 1 }}
                         transition={{ delay: index * 0.1 + i * 0.05 }}
                         whileHover={{ scale: 1.05 }}
                       >
                        {skill}
                      </motion.span>
                    ))}
                  </div>
                </motion.div>
              ))}
            </motion.div>
          )}

          {activeSection === 'personal' && (
            <motion.div variants={itemVariants} className="personal-section">
                             <motion.div
                 className="personal-card"
                 style={{
                   background: getCardGradient(),
                   border: `1px solid ${colors.border}`,
                   boxShadow: `0 8px 32px ${colors.shadow}`,
                   backdropFilter: 'blur(10px)'
                 }}
                 variants={cardVariants}
                 whileHover="hover"
               >
                <h2 style={{ color: colors.secondary }}>Personal</h2>
                <div className="personal-items">
                                     <motion.div
                     className="personal-item"
                     initial={{ opacity: 0, x: -20 }}
                     animate={{ opacity: 1, x: 0 }}
                     transition={{ delay: 0.2 }}
                     style={{
                       background: `linear-gradient(135deg, ${colors.primary}10 0%, ${colors.secondary}10 100%)`,
                       border: `1px solid ${colors.border}`
                     }}
                   >
                     <span style={{ color: colors.accent }}>üêï</span>
                     <span>Does dog sport training with two dogs</span>
                   </motion.div>
                                     <motion.div
                     className="personal-item"
                     initial={{ opacity: 0, x: -20 }}
                     animate={{ opacity: 1, x: 0 }}
                     transition={{ delay: 0.4 }}
                     style={{
                       background: `linear-gradient(135deg, ${colors.primary}10 0%, ${colors.secondary}10 100%)`,
                       border: `1px solid ${colors.border}`
                     }}
                   >
                     <span style={{ color: colors.accent }}>üç∑</span>
                     <span>Prefers Georgian sweet red wine or a cold IPA</span>
                   </motion.div>
                                     <motion.div
                     className="personal-item"
                     initial={{ opacity: 0, x: -20 }}
                     animate={{ opacity: 1, x: 0 }}
                     transition={{ delay: 0.6 }}
                     style={{
                       background: `linear-gradient(135deg, ${colors.primary}10 0%, ${colors.secondary}10 100%)`,
                       border: `1px solid ${colors.border}`
                     }}
                   >
                     <span style={{ color: colors.accent }}>üèîÔ∏è</span>
                     <span>Enjoys outdoor activities like hiking</span>
                   </motion.div>
                                     <motion.div
                     className="personal-item"
                     initial={{ opacity: 0, x: -20 }}
                     animate={{ opacity: 1, x: 0 }}
                     transition={{ delay: 0.8 }}
                     style={{
                       background: `linear-gradient(135deg, ${colors.primary}10 0%, ${colors.secondary}10 100%)`,
                       border: `1px solid ${colors.border}`
                     }}
                   >
                     <span style={{ color: colors.accent }}>üìç</span>
                     <span>Is relocating to Billund area</span>
                   </motion.div>
                </div>
              </motion.div>
            </motion.div>
          )}
        </motion.div>
      </AnimatePresence>
    </motion.div>
  );
};

export default Portfolio;
